import React, { useState } from 'react';
import { motion } from 'framer-motion';
import './Chatbot.css';

const Chatbot = () => {
  const [input, setInput] = useState('');
  const [messages, setMessages] = useState([
    { sender: 'bot', text: 'Hey! I‚Äôm Lily üë©‚Äçüç≥. What recipe are you looking for today?' }
  ]);

  const handleSendMessage = async () => {
    if (!input.trim()) return;

    const newMessage = { sender: 'user', text: input };
    setMessages([...messages, newMessage]);

    try {
      const response = await fetch('http://localhost:5000/get-recipe', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ user_input: input })
      });
      const data = await response.json();
      const recipeText = data.recipe || "Sorry, I couldn't find that. Can you rephrase?";
      setMessages(prev => [...prev, { sender: 'bot', text: recipeText }]);
    } catch (error) {
      setMessages(prev => [...prev, { sender: 'bot', text: 'Oops! Something went wrong.' }]);
    }

    setInput('');
  };

  const handleVoiceInput = () => {
    const recognition = new window.webkitSpeechRecognition();
    recognition.lang = 'en-US';
    recognition.onresult = (event) => {
      const voiceInput = event.results[0][0].transcript;
      setInput(voiceInput);
    };
    recognition.start();
  };

  return (
    <div className="chat-background-wrapper">
      <video autoPlay loop muted className="bg-video">
        <source src="/background.mp4" type="video/mp4" />
        Your browser does not support the video tag.
      </video>

      <div className="chat-wrapper">
        <div className="chat-header">Lily Chef Bot üçΩÔ∏è</div>
        <div className="chat-container">
          <div className="chat-box">
            {messages.map((msg, index) => (
              <motion.div
                key={index}
                className={msg.sender === 'bot' ? 'bot-message' : 'user-message'}
                initial={{ opacity: 0, x: msg.sender === 'bot' ? -50 : 50 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ duration: 0.3 }}
              >
                {msg.text}
              </motion.div>
            ))}
          </div>
          <div className="chat-input">
            <input
              type="text"
              placeholder="Ask me a recipe or say what ingredients you have..."
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyDown={(e) => e.key === 'Enter' && handleSendMessage()}
            />
            <button onClick={handleSendMessage}>‚û§</button>
            <button className="mic-button" onClick={handleVoiceInput}>üé§</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Chatbot;
