import React, { useState } from "react";
import "./Chatbot.css";
import { motion } from "framer-motion";

const Chatbot = () => {
  const [messages, setMessages] = useState([
    { text: "Hello! Ask me for a recipe or any question.", sender: "bot" }
  ]);
  const [input, setInput] = useState("");

  const extractDishName = (text) => {
    const match = text.match(/(?:recipe for|make|prepare|cook)?\s*([a-zA-Z ]+)/i);
    return match ? match[1].trim() : text;
  };

  const handleSendMessage = async () => {
    if (!input.trim()) return;
    const userMessage = { text: input, sender: "user" };
    setMessages((prev) => [...prev, userMessage]);

    const dishName = extractDishName(input);
    try {
      const response = await fetch(`http://127.0.0.1:5000/get-recipe?name=${encodeURIComponent(dishName)}`);
      const data = await response.json();

      if (data.recipe) {
        const botMessage = {
          text: (
            <>
              <strong>Here is the recipe for {data.recipe.title}:</strong>
              <ul className="recipe-list">
                {data.recipe.ingredients.map((item, index) => (
                  <li key={index}>{item}</li>
                ))}
              </ul>
              <div className="recipe-steps">
                {data.recipe.steps.map((step, index) => (
                  <p key={index} className="recipe-step">Step {index + 1}: {step}</p>
                ))}
              </div>
            </>
          ),
          sender: "bot"
        };
        setMessages((prev) => [...prev, botMessage]);
      } else {
        // Ask user for clarification
        setMessages((prev) => [...prev, { text: "Sorry, I couldn't find that recipe. Please check the name and try again.", sender: "bot" }]);
      }
    } catch (error) {
      // Handle general OpenAI queries
      try {
        const aiResponse = await fetch("http://127.0.0.1:5000/ask-ai", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ query: input })
        });
        const aiData = await aiResponse.json();
        setMessages((prev) => [...prev, { text: aiData.response, sender: "bot" }]);
      } catch (error) {
        setMessages((prev) => [...prev, { text: "Error processing request.", sender: "bot" }]);
      }
    }
    setInput("");
  };

  return (
    <div className="chat-container">
      <div className="chat-box">
        {messages.map((msg, index) => (
          <motion.div
            key={index}
            className={msg.sender === "bot" ? "bot-message" : "user-message"}
            initial={{ opacity: 0, x: msg.sender === "bot" ? -50 : 50 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.3 }}
          >
            {msg.text}
          </motion.div>
        ))}
      </div>
      <div className="chat-input">
        <input
          type="text"
          placeholder="Ask me a recipe or a question..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyPress={(e) => e.key === "Enter" && handleSendMessage()}
        />
        <button onClick={handleSendMessage}>Send</button>
      </div>
    </div>
  );
};

export default Chatbot;
