import React, { useState } from "react";
import "./Chatbot.css";
import { motion } from "framer-motion";

const Chatbot = () => {
  const [messages, setMessages] = useState([
    { text: "Hello! Ask me for a recipe.", sender: "bot" }
  ]);
  const [input, setInput] = useState("");

  const handleSendMessage = async () => {
    if (!input.trim()) return;

    const userMessage = { text: input, sender: "user" };
    setMessages((prev) => [...prev, userMessage]);

    try {
      const response = await fetch(
        `http://127.0.0.1:5000/get-recipe?name=${encodeURIComponent(input)}` // Backend URL
      );
      const data = await response.json();

      if (data.recipe) {
        const botMessage = { text: `Here is the recipe for ${data.recipe.title}: ${data.recipe.ingredients}`, sender: "bot" };
        setMessages((prev) => [...prev, botMessage]);
      } else {
        setMessages((prev) => [...prev, { text: "Sorry, recipe not found.", sender: "bot" }]);
      }
    } catch (error) {
      setMessages((prev) => [...prev, { text: "Error fetching recipe.", sender: "bot" }]);
    }

    setInput(""); // Clear input
  };

  return (
    <div className="chat-container">
      <div className="chat-box">
        {messages.map((msg, index) => (
          <motion.div
            key={index}
            className={msg.sender === "bot" ? "bot-message" : "user-message"}
            initial={{ opacity: 0, x: msg.sender === "bot" ? -50 : 50 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.3 }}
          >
            {msg.text}
          </motion.div>
        ))}
      </div>
      <div className="chat-input">
        <input
          type="text"
          placeholder="Ask for a recipe..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyPress={(e) => e.key === "Enter" && handleSendMessage()}
        />
        <button onClick={handleSendMessage}>Send</button>
      </div>
    </div>
  );
};

export default Chatbot;
