import React, { useState } from "react";
import { motion } from "framer-motion";
import "./Chatbot.css";

const Chatbot = () => {
  const [messages, setMessages] = useState([
    { text: "Hello! I'm Chintu. Ask me for any recipe!", sender: "bot" },
  ]);
  const [input, setInput] = useState("");

  const sendMessage = async () => {
    if (!input.trim()) return;

    const userMessage = { text: input, sender: "user" };
    setMessages((prevMessages) => [...prevMessages, userMessage]);
    setInput("");

    try {
      const response = await fetch(
        `http://localhost:5000/get-recipe?name=${encodeURIComponent(input)}`
      );
      const data = await response.json();

      const botMessage =
        data.recipe && data.recipe.title
          ? { text: `Here is the recipe for ${data.recipe.title}: ${data.recipe.ingredients.join(", ")}`, sender: "bot" }
          : { text: "Sorry, I couldn't find that recipe.", sender: "bot" };

      setMessages((prevMessages) => [...prevMessages, botMessage]);
    } catch (error) {
      setMessages((prevMessages) => [...prevMessages, { text: "Error fetching recipe.", sender: "bot" }]);
    }
  };

  return (
    <div className="chat-container">
      <div className="chat-box">
        {messages.map((msg, index) => (
          <motion.div
            key={index}
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.3 }}
            className={msg.sender === "bot" ? "bot-message" : "user-message"}
          >
            {msg.text}
          </motion.div>
        ))}
      </div>
      <div className="chat-input">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Ask Chintu for a recipe..."
        />
        <motion.button
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.9 }}
          onClick={sendMessage}
        >
          Send
        </motion.button>
      </div>
    </div>
  );
};

export default Chatbot;
