import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';
import AddRecipeForm from './AddRecipeForm';
import SearchForm from './SearchForm';

const App = () => {
  const [recipes, setRecipes] = useState([]);
  const [searchedRecipe, setSearchedRecipe] = useState(null);
  const [message, setMessage] = useState('');

  const fetchRecipes = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/recipes');
      setRecipes(res.data);
    } catch (err) {
      console.error('Error fetching recipes:', err);
      setMessage('Error fetching recipes');
    }
  };

  const handleSearch = async (query) => {
    try {
      const res = await axios.post('http://localhost:5000/get-recipe', { query });
      setSearchedRecipe(res.data.recipe || { title: 'Not found', ingredients: '-', steps: '-' });
    } catch (err) {
      console.error('Error searching recipe:', err);
      setSearchedRecipe({ title: 'Error occurred', ingredients: '-', steps: '-' });
    }
  };

  useEffect(() => {
    fetchRecipes();
  }, []);

  return (
    <div className="container">
      <h1>üç≤ Cooking Recipe Chatbot</h1>

      <AddRecipeForm onRecipeAdded={fetchRecipes} />

      <SearchForm onSearch={handleSearch} />

      {searchedRecipe && (
        <div className="search-result">
          <h3>{searchedRecipe.title}</h3>
          <p><strong>Ingredients:</strong> {searchedRecipe.ingredients}</p>
          <p><strong>Steps:</strong> {searchedRecipe.steps}</p>
        </div>
      )}

      {message && <p className="message">{message}</p>}

      <h2>üìã All Recipes</h2>
      <ul className="recipe-list">
        {recipes.map((r, idx) => (
          <li key={idx}>
            <strong>{r.title}</strong><br />
            <em>Ingredients:</em> {r.ingredients}<br />
            <em>Steps:</em> {r.steps}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default App;
